// Import the necessary modules.
// @flow
import type {
  $Request,
  $Response,
  NextFunction
} from 'express'
import type { MongooseModel } from 'mongoose'

import {
  BaseContentController,
  type ContentService
} from 'pop-api'

/**
 * Base class for getting content from endpoints.
 * @implements {BaseContentController}
 * @type {ContentController}
 */
export default class ContentController extends BaseContentController {

  /**
   * The base path for the routes.
   * @type {string}
   */
  basePath: string

  /**
   * The service of the content controller.
   * @type {ContentService}
   */
  service: ContentService

  /**
   * Register the default methods to the default routes.
   * @param {!Object} router - The express router to register the routes to.
   * @param {?PopApi} [PopApi] - The PopApi instance.
   * @returns {undefined}
   */
  registerRoutes(router: any, PopApi?: any): void {
    const t = this.basePath

    router.get(`/${t}s`, this.getContents.bind(this))
    router.get(`/${t}s/:page`, this.getPage.bind(this))
    router.get(`/${t}/:id`, this.getContent.bind(this))
    router.get(`/random/${t}`, this.getRandomContent.bind(this))
  }

  /**
   * Default method to sort the items.
   * @param {!string} sort - The property to sort on.
   * @param {!number} order - The way to sort the property.
   * @returns {Object} - The sort object.
   */
  sortContent(sort: string, order: number): Object {
    const s = sort.toLowerCase()
    const sortObj = {
      score: {
        $meta: 'textScore'
      }
    }

    switch (s) {
      case 'name':
        return {
          ...sortObj,
          title: order
        }
      case 'rating':
        return {
          ...sortObj,
          'rating.votes': order,
          'rating.percentage': order
        }
      case 'released':
      case 'updated':
        return {
          ...sortObj,
          latest_episode: order,
          released: order
        }
      case 'trending':
        return {
          ...sortObj,
          'rating.watching': order
        }
      case 'year':
        return {
          ...sortObj,
          year: order
        }
      default:
        return {
          ...sortObj,
          'rating.votes': order,
          'rating.precentage': order,
          'rating.watching': order
        }
    }
  }

  /**
   * Get content from one page.
   * @param {!Object} req - The ExpressJS request object.
   * @param {!Object} res - The ExpressJS response object.
   * @param {!Function} next - The ExpressJS next function.
   * @returns {Promise<Array<Object>, Error>} - The content of one
   * page.
   */
  getPage(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): Promise<Array<MongooseModel> | mixed> {
    const { page } = req.params
    const { sort, order, genre, keywords } = req.query

    const o = parseInt(order, 10) ? parseInt(order, 10) : -1
    const s = typeof sort === 'string' ? this.sortContent(sort, o) : ''

    const query = {
      ...this.service.query
    }

    if (typeof genre === 'string' && genre.toLowerCase() !== 'all') {
      if (genre.match(/science[-\s]fuction/i) || genre.match(/sci[-\s]fi/i)) {
        query.genres = 'science-fiction'
      } else {
        query.genres = genre.toLowerCase()
      }
    }

    if (typeof keywords === 'string') {
      query.$text = {
        $search: keywords
      }
    }

    return this.service.getPage(s, Number(page), query)
      .then(content => this.checkEmptyContent(res, content))
      .catch(err => next(err))
  }

}

// Import the necessary modules.
// @flow
import { join } from 'path'
import { existsSync } from 'fs'
import type {
  $Request,
  $Response,
  NextFunction
} from 'express'

import { IController } from 'pop-api'

/**
 * Class for downloading export collections.
 * @type {ExportController}
 * @implements {IController}
 */
export default class ExportController extends IController {

  /**
   * Register the routes for the export controller to the Express instance.
   * @param {!Object} router - The express router to register the routes to.
   * @param {?PopApi} [PopApi] - The PopApi instance.
   * @returns {undefined}
   */
  registerRoutes(router: any, PopApi?: any): void {
    router.get('/exports/:collection', this.getExport)
  }

  /**
   * Download the export of a collection.
   * @param {!Object} req - The ExpressJS request object.
   * @param {!Object} res - The ExpressJS response object.
   * @param {!Function} next - The ExpressJS next function.
   * @returns {Object|Error} - The download request of an export of a
   * collection.
   */
  getExport(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): Object | mixed {
    const { collection } = req.params

    if (collection.match(/(anime|movie|show)s?/i)) {
      process.env.TEMP_DIR = process.env.TEMP_DIR
        ? process.env.TEMP_DIR
        : join(...[
          __dirname,
          '..',
          '..',
          'tmp'
        ])

      const tempDir = process.env.TEMP_DIR
      let jsonFile = join(...[
        tempDir,
        `${collection}.json`
      ])
      if (existsSync(jsonFile)) {
        return res.download(jsonFile)
      }

      jsonFile = join(...[
        tempDir,
        `${collection}s.json`
      ])
      if (existsSync(jsonFile)) {
        return res.download(jsonFile)
      }

      return next(new Error(`Error: no such file found for '${jsonFile}'`))
    }

    const msg = `Error: '${collection}' is not a valid collection to export.`
    return next(new Error(msg))
  }

}

// Import the necessary modules.
// @flow
import fs from 'fs'
import { join } from 'path'
import {
  ApiError,
  IController,
  PopApi,
  utils
} from 'pop-api'
import type {
  $Request,
  $Response,
  NextFunction
} from 'express'

import ContentController from './ContentController'
import {
  AnimeShow as Anime,
  Movie,
  Show
} from '../models'
import {
  name,
  repository,
  version
} from '../../package.json'

/**
 * Class for displaying information about the server the API is running on.
 * @type {IndexController}
 * @implements {IController}
 */
export default class IndexController extends IController {

  /**
   * The name of the server. Default is `serv01`.
   * @type {string}
   */
  static _Server: string = 'serv01'

  /**
   * Register the routes for the index controller to the Express instance.
   * @param {!Object} router - The express router to register the routes to.
   * @param {?PopApi} [PopApi] - The PopApi instance.
   * @returns {undefined}
   */
  registerRoutes(router: any, PopApi?: any): void {
    router.get('/status', this.getIndex)
    router.get('/logs/error', this.getErrorLog)
  }

  /**
   * Get general information about the server.
   * @param {!Object} req - The ExpressJS request object.
   * @param {!Object} res - The ExpressJS response object.
   * @param {!Function} next - The ExpressJS next function.
   * @returns {Promise<Object, Error>} - General information about the server.
   */
  async getIndex(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): Promise<Object | mixed> {
    try {
      const commit = await utils.executeCommand('git', [
        'rev-parse',
        '--short',
        'HEAD'
      ])

      const query = ContentController.Query
      const totalAnimes = await Anime.count(query).exec()
      const totalMovies = await Movie.count(query).exec()
      const totalShows = await Show.count(query).exec()

      return res.json({
        repo: repository.url,
        server: IndexController._Server,
        status: await PopApi.scraper.getStatus(),
        totalAnimes,
        totalMovies,
        totalShows,
        updated: await PopApi.scraper.getUpdated(),
        uptime: process.uptime() | 0, // eslint-disable-line no-bitwise
        version,
        commit
      })
    } catch (err) {
      return next(err)
    }
  }

  /**
   * Displays the 'popcorn-api.log' file.
   * @param {!Object} req - The ExpressJS request object.
   * @param {!Object} res - The ExpressJS response object.
   * @param {!Function} next - The ExpressJS next function.
   * @returns {Object|Error} - The content of the log file.
   */
  getErrorLog(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): Object | mixed {
    process.env.TEMP_DIR = typeof process.env.TEMP_DIR === 'string'
      ? process.env.TEMP_DIR
      : join(...[
        __dirname,
        '..',
        '..',
        'tmp'
      ])

    const root = process.env.TEMP_DIR
    const file = `${name}.log`
    const filePath = join(...[
      process.env.TEMP_DIR,
      file
    ])

    if (fs.existsSync(filePath)) {
      return res.sendFile(file, {
        root,
        headers: {
          'Content-Type': 'text/plain; charset=UTF-8'
        }
      })
    }

    return next(new ApiError({
      message: `Could not find file: '${filePath}'`
    }))
  }

}

// Import the necessary modules.
// @flow
import { ContentService } from 'pop-api'

import ContentController from './ContentController'
import ExportController from './ExportController'
import IndexController from './IndexController'
import {
  AnimeShow as Anime,
  Movie,
  Show
} from '../models'

/**
 * Object used as a base projections for content.
 * @type {Object}
 */
const baseProjection: Object = {
  _id: 1,
  imdb_id: 1,
  title: 1,
  year: 1,
  slug: 1,
  synopsis: 1,
  genres: 1,
  images: 1,
  rating: 1,
  type: 1
}

/**
 * Object used for the projection of movies.
 * @type {Object}
 */
const movieProjection: Object = {
  ...baseProjection,
  synopsis: 1,
  runtime: 1,
  released: 1,
  trailer: 1,
  certification: 1,
  torrents: 1
}

/**
 * Object used for the projection of shows.
 * @type {Object}
 */
const showProjection: Object = {
  ...baseProjection,
  tvdb_id: 1,
  num_seasons: 1
}

/**
 * Object used for the projection of animes.
 * @type {Object}
 */
const animeProjection: Object = {
  ...movieProjection,
  ...showProjection
}

/**
 * Object used to query for content.
 * @type {Object}
 */
const query: Object = {
  $or: [{
    num_seasons: {
      $gt: 0
    }
  }, {
    torrents: {
      $exists: true
    }
  }]
}

/**
 * The controllers used by the setup process of registering them.
 * @type {Array<Object>}
 */
export default [{
  Controller: IndexController,
  args: {}
}, {
  Controller: ExportController,
  args: {}
}, {
  Controller: ContentController,
  args: {
    basePath: 'anime',
    service: new ContentService({
      Model: Anime,
      projection: animeProjection,
      query
    })
  }
}, {
  Controller: ContentController,
  args: {
    basePath: 'movie',
    service: new ContentService({
      Model: Movie,
      projection: movieProjection,
      query
    })
  }
}, {
  Controller: ContentController,
  args: {
    basePath: 'show',
    service: new ContentService({
      Model: Show,
      projection: showProjection,
      query
    })
  }
}]

// Import the necessary modules.
// @flow
import dotenv from 'dotenv'

import { isMaster } from 'cluster'
import { join } from 'path'
import {
  Database,
  HttpServer,
  Logger,
  Routes,
  PopApi
} from 'pop-api'
import {
  Cron,
  PopApiScraper
} from 'pop-api-scraper'

import controllers from './controllers'
import providers from './scraper'
import { Cli } from './middleware'
import {
  name,
  version
} from '../package.json'
dotenv.config();

/** Setup the api. */
(async () => {
  try {
    providers.map(p => {
      const { Provider, args } = p
      PopApiScraper.use(Provider, args)
    })

    process.env.TEMP_DIR = process.env.TEMP_DIR || join(...[
      __dirname,
      '..',
      'tmp'
    ])
    const logDir = process.env.TEMP_DIR
    await PopApi.init({
      name,
      version,
      logDir,
      controllers,
      statusPath: join(...[logDir, 'status.json']),
      updatedPath: join(...[logDir, 'updated.json'])
    }, [
      Cli,
      Logger,
      Database,
      Routes,
      HttpServer,
      PopApiScraper,
      Cron
    ])

    // TODO: make scraping start wth 'start' param in 'init'.
    if (isMaster && PopApi.startScraper) {
      PopApi.scraper.scrape()
    }
  } catch (err) {
    throw err
  }
})()

// Import the necessary modules.
// @flow
/* eslint-disable no-console */
import bytes from 'bytes'
import fs from 'fs'
import inquirer from 'inquirer'
import parseTorrent from 'parse-torrent'
import path from 'path'
import webtorrentHealth from 'webtorrent-health'
import {
  Cli as BaseCli,
  Database
} from 'pop-api'

import promptSchemas from './promptschemas.js'
import {
  AnimeMovie,
  AnimeShow,
  Movie,
  Show
} from '../models'
import {
  MovieHelper,
  ShowHelper
} from '../scraper/helpers'
import {
  MovieProvider,
  ShowProvider
} from '../scraper/providers'

/**
 * Class The class for the command line interface.
 * @type {Cli}
 */
export default class Cli extends BaseCli {

  /**
   * The command line parser to process the Cli inputs.
   * @type {Command}
   */
  program: Object

  /**
   * The name of the Cli program.
   * @type {string}
   */
  name: string

  /**
   * The verion of the Cli program.
   * @type {string}
   */
  version: string

  /**
   * The name of the Cli provider. Default is `Cli`.
   * @type {string}
   */
  static _Name: string = 'Cli'

  /**
   * The database middleware from `pop-api`.
   * @type {Database}
   */
  _database: Database

  /**
   * Create a CLI object.
   * @param {!PopApi} PopApi - The PopApi instance to bind the cli to.
   * @param {!Ojbect} options - The options for the cli.
   * @param {?Array<string>} options.argv - The arguments to be parsed by
   * commander.
   * @param {!string} options.name - The name of the Cli program.
   * @param {!string} [options.version] - The version of the Cli program.
   */
  constructor(PopApi: any, { argv, name, version }: Object): void {
    super(PopApi, { name, version })

    /**
      * The database middleware from `pop-api`.
      * @type {Database}
      */
    this.database = new Database(PopApi, {
      database: name
    })

    this.run(PopApi, argv)
  }

  /**
   * Initiate the options for the Cli.
   * @param {!string} version - The version of the Cli program.
   * @returns {undefined}
   */
  initOptions(version: string): void {
    super.initOptions(version)

    return this.program
      .option('-s, --start', 'Start the scraping process')
      .option('--content <type>',
        'Add content to the MongoDB database (animemovie|animeshow|movie|show).',
        /^(animemovie|animeshow|movie|show)$/i, false)
      .option(
        '--providers <env>',
        'Add provider configurations',
        /^(development|production|test)$/i
      )
      .option('--export <collection>',
        'Export a collection to a JSON file.',
        /^(anime|movie|show)$/i, false)
      .option('--import <collection>', 'Import a JSON file to the database.')
  }

  /**
   * Method for displaying the --help option
   * @returns {undefined}
   */
  getHelp(): void {
    const baseHelp = super.getHelp()
    return baseHelp.concat([
      `    $ ${this.name} --content <animemovie|animeshow|movie|show>`,
      `    $ ${this.name} --provider`,
      `    $ ${this.name} --export <anime|movie|show>`,
      `    $ ${this.name} --import <path-to-json>`
    ])
  }

  /**
   * Return a torrent object for a movie.
   * @param {!string} magnet - The magnet url to bind.
   * @param {!Object} health - The health object for seeders and peers.
   * @param {!Object} remote - The remote data object from 'parseTorrent'.
   * @returns {Object} - A torrent object for a movie.
   */
  _movieTorrent(magnet: string, health: Object, remote: Object): Object {
    return {
      url: magnet,
      seeds: health.seeds,
      peers: health.peers,
      size: remote.length,
      filesize: bytes(remote.length),
      provider: Cli._Name
    }
  }

  /**
   * Return a torrent object for a show.
   * @param {!string} magnet - The magnet url to bind.
   * @param {!Object} health - The health object for seeders and peers.
   * @returns {Object} - A torrent object for a show.
   */
  _showTorrent(magnet: string, health: Object): Object {
    return {
      url: magnet,
      seeds: health.seeds,
      peers: health.peers,
      provider: Cli._Name
    }
  }

  /**
   * Get a torrent object based on the type.
   * @param {!string} link - The link to bind to the torrent object.
   * @param {!string} type - The type of torrent object (movie|show).
   * @returns {Promise<Object, undefined>} - A torrent object for a movie or
   * show.
   */
  _getTorrent(link: string, type: string): Promise<Object | void> {
    return new Promise((resolve, reject) => {
      return parseTorrent.remote(link, (err, remote) => {
        if (err) {
          return reject(err)
        }

        const magnet = parseTorrent.toMagnetURI(remote)
        return webtorrentHealth(magnet).then(health => {
          const torrent = type === 'movie'
            ? this._movieTorrent(magnet, health, remote)
            : this._showTorrent(magnet, health)
          return resolve(torrent)
        })
      })
    })
  }

  /**
   * Handle the --content CLI option to insert a movie torrent.
   * @param {!string} t - The content type to add to the database.
   * @param {!boolean} isAnime=false - True if the content is anime.
   * @returns {Promise<Movie, Error>} - The inserted movie.
   */
  _moviePrompt(t: string, isAnime: boolean = false): Promise<Movie | Error> {
    const { imdb, torrent, movieQuality, language } = promptSchemas
    const movieSchema: Array<Object> = [
      imdb,
      torrent,
      movieQuality,
      language
    ]

    return this.database.connect().then(() => {
      return inquirer.prompt(movieSchema).then(res => {
        const { imdb, quality, language, torrent } = res
        const movie = {
          slugYear: imdb,
          torrents: {}
        }
        const contentType = MovieProvider.ContentTypes.Movie
        const movieProvider = new MovieProvider({}, {})
        movieProvider.setConfig({
          name: Cli._Name,
          Helper: MovieHelper,
          Model: isAnime ? AnimeMovie : Movie,
          contentType
        })

        return this._getTorrent(torrent, contentType).then(res => {
          movieProvider.attachTorrent({
            movie,
            quality,
            torrent: res,
            lang: language
          })

          return movieProvider.getContent(movie)
        })
      })
    }).then(() => this.database.disconnect())
      .then(() => process.exit(0))
      .catch(err => {
        console.error(`An error occurred: '${err}'`)
        return process.exit(1)
      })
  }

  /**
   * Handle the --content CLI option to insert a movie torrent.
   * @param {!string} t - The content type to add to the database.
   * @param {!boolean} isAnime=false - True if the content is anime.
   * @returns {Promise<Show, Error>} - The inserted show.
   */
  _showPrompt(t: string, isAnime: boolean = false): Promise<Show | Error> {
    const {
      imdb,
      torrent,
      showQuality,
      season,
      episode,
      dateBased
    } = promptSchemas
    const showSchema: Array<Object> = [
      imdb,
      torrent,
      showQuality,
      season,
      episode,
      dateBased
    ]

    return this.database.connect().then(() => {
      return inquirer.prompt(showSchema).then(res => {
        const { imdb, season, episode, quality, dateBased, torrent } = res
        const show = {
          slug: imdb,
          dateBased,
          episodes: {}
        }
        const contentType = MovieProvider.ContentTypes.Show
        const showProvider = new ShowProvider({}, {})
        showProvider.setConfig({
          name: Cli._Name,
          Helper: ShowHelper,
          Model: isAnime ? AnimeShow : Show,
          contentType
        })

        return this._getTorrent(torrent, contentType).then(res => {
          showProvider.attachTorrent({
            show,
            torrent: res,
            season,
            episode,
            quality
          })

          return showProvider.getContent(show)
        })
      })
    }).then(() => this.database.disconnect())
      .then(() => process.exit(0))
      .catch(err => {
        console.error(`An error occurred: '${err}'`)
        return process.exit(1)
      })
  }

  /**
   * Handle the --content CLI option.
   * @param {!string} t - The content type to add to the database.
   * @returns {Promise<Movie|Show, Error>|undefined} - The inserted movie or
   * show.
   */
  _content(t: string): Promise<Movie | Show | Error> {
    switch (t) {
      case 'animemovie':
        return this._moviePrompt(t, true)
      case 'movie':
        return this._moviePrompt(t)
      case 'animeshow':
        return this._showPrompt(t, true)
      case 'show':
        return this._showPrompt(t)
      default:
        console.error(`'${t}' is not a valid option for content!`)
        return Promise.reject(process.exit(1))
    }
  }

  /**
   * Handle the --export CLI option.
   * @param {!string} e - The collection to export.
   * @returns {Promise<string, undefined>} - The promise to export a collection.
   */
  _export(e: string): Promise<string | void> {
    process.env.TEMP_DIR = process.env.TEMP_DIR || path.join(...[
      __dirname,
      '..',
      '..',
      'tmp'
    ])
    const tempDir = process.env.TEMP_DIR

    return this.database.exportFile(e, path.join(...[
      tempDir,
      `${e}s.json`
    ]))
      .then(() => process.exit(0))
      .catch(err => {
        console.error(`An error occurred: ${err}`)
        return process.exit(1)
      })
  }

  /**
   * Handle the --import CLI option.
   * @param {!string} i - The collection to import.
   * @throws {Error} - Error: no such file found for 'JSON_FILE'
   * @returns {Promise<string, undefined>|undefined} - The promise to import a
   * collection.
   */
  _import(i: string): Promise<string | void> {
    if (!fs.existsSync(i)) {
      console.error(`File '${i}' does not exists!`)
      return Promise.reject(process.exit(1))
    }

    const { confirm } = promptSchemas
    return inquirer.prompt([confirm]).then(({ confirm }) => {
      if (confirm) {
        return this.database.importFile(path.basename(i, '.json'), i)
      }

      return process.exit(0)
    }).catch(err => {
      console.error(`An error occurred: ${err}`)
      return process.exit(1)
    })
  }

  /**
   * Run the Cli program.
   * @param {!PopApi} PopApi - The PopApi instance to bind the options to.
   * @param {?Array<string>} argv - The arguments to be parsed by commander.
   * @returns {undefined}
   */
  run(PopApi: any, argv?: Array<string>): any {
    if (argv) {
      this.program.parse(argv)
    }

    if (this.program.content) {
      return this._content(this.program.content)
    } else if (this.program.export) {
      return this._export(this.program.export)
    } else if (this.program.import) {
      return this._import(this.program.import)
    }

    if (this.program.start) {
      PopApi.startScraper = true
    }

    return super.run(PopApi)
  }

}

// @flow

/**
 * The available qualities for movies.
 * @type {Array<string>}
 */
const _movieQualities: Array<string> = ['720p', '1080p']

/**
 * The available qualities for shows.
 * @type {Array<string>}
 */
const _showQualities: Array<string> = ['480p', ..._movieQualities]

/**
 * The quality property.
 * @type {Object}
 */
const _baseQuality: Object = {
  type: 'list',
  name: 'quality'
}

/**
 * The imdb property.
 * @type {Object}
 */
const imdb: Object = {
  type: 'input',
  name: 'imdb',
  message: 'The imdb id of the show/movie to add (tt1234567)',
  validate(value: string): boolean | string {
    const pass = /^(tt\d{7})/i.test(value)
    if (pass) {
      return true
    }

    return 'Not a valid imdb id.'
  }
}

/**
 * The torrent property.
 * @type {Object}
 */
const torrent: Object = {
  type: 'input',
  name: 'torrent',
  message: 'The link to the torrent to add',
  validate(value: string): boolean | string {
    if (value && typeof value === 'string') {
      return true
    }

    return 'Not a valid torrent.'
  }
}

/**
 * The movie quality property
 * @type {Object}
 */
const movieQuality: Object = {
  ..._baseQuality,
  choices: _movieQualities,
  message: 'The quality of the torrent (720p | 1080p)'
}

/**
 * The show quality property.
 * @type {Object}
 */
const showQuality: Object = {
  ..._baseQuality,
  choices: _showQualities,
  message: 'The quality of the torrent (480p | 720p | 1080p)'
}

/**
 * The language property.
 * @type {Object}
 */
const language: Object = {
  type: 'input',
  name: 'language',
  message: 'The language of the torrent to add (en, fr, jp)',
  validate(value: string): boolean | string {
    const pass = /^([a-zA-Z]{2})/i.test(value)
    if (pass) {
      return true
    }

    return 'Not a valid language'
  }
}

/**
 * The season property.
 * @type {Object}
 */
const season: Object = {
  type: 'input',
  name: 'season',
  message: 'The season number of the torrent',
  validate(value: string): boolean | string {
    const pass = /^(\d+)/i.test(value)
    if (pass) {
      return true
    }

    return 'Not a valid season.'
  }
}

/**
 * The episode property.
 * @type {Object}
 */
const episode: Object = {
  type: 'input',
  name: 'episode',
  message: 'The episode number of the torrent',
  validate(value: string): boolean | string {
    const pass = /^(\d+)/i.test(value)
    if (pass) {
      return true
    }

    return 'Not a valid episode.'
  }
}

/**
 * The dateBased property.
