/**
 * Class for saving shows.
 * @extends {AbstractHelper}
 * @type {ShowHelper}
 */
export default class ShowHelper extends AbstractHelper {

  /**
   * Update the number of seasons of a given show.
   * @param {!AnimeShow|Show} show - The show to update the number of seasons.
   * @returns {AnimeShow|Show} - A newly updated show.
   */
  async _updateNumSeasons(show: AnimeShow | Show): AnimeShow | Show {
    const saved = await this.Model.findOneAndUpdate({
      _id: show.imdb_id
    }, new this.Model(show), {
      new: true,
      upsert: true
    })

    const distinct = await this.Model.distinct('episodes.season', {
      _id: saved.imdb_id
    }).exec()
    saved.num_seasons = distinct.length

    return this.Model.findOneAndUpdate({
      _id: saved.imdb_id
    }, new this.Model(saved), {
      new: true,
      upsert: true
    })
  }

  /**
   * Update the torrents for an existing show.
   * @param {!Object} matching - The matching episode of new the show.
   * @param {!Object} found - The matching episode existing show.
   * @param {!AnimeShow|Show} show - The show to merge the episodes to.
   * @param {!string} quality - The quality of the torrent.
   * @returns {AnimeShow|Show} - A show with merged torrents.
   */
  _updateEpisode(
    matching: Object,
    found: Object,
    show: AnimeShow | Show,
    quality: string
  ): AnimeShow | Show {
    const index = show.episodes.indexOf(matching)

    const foundTorrents = found.torrents[quality]
    let matchingTorrents = matching.torrents[quality]

    if (foundTorrents && matchingTorrents) {
      let update = false

      if (
        foundTorrents.seeds > matchingTorrents.seeds ||
        foundTorrents.url === matchingTorrents.url
      ) {
        update = true
      }

      if (update) {
        if (quality === '480p') {
          matching.torrents['0'] = foundTorrents
        }

        matchingTorrents = foundTorrents
      }
    } else if (foundTorrents && !matchingTorrents) {
      if (quality === '480p') {
        matching.torrents['0'] = foundTorrents
      }

      matchingTorrents = foundTorrents
    }

    show.episodes.splice(index, 1, matching)
    return show
  }

  /**
   * Update a given show with it's associated episodes.
   * @param {!AnimeShow|Show} show - The show to update its episodes.
   * @returns {AnimeShow|Show} - A newly updated show.
   */
  async _updateEpisodes(show: AnimeShow | Show): AnimeShow | Show {
    try {
      let s = show
      const found = await this.Model.findOne({
        _id: s.imdb_id
      })
      if (!found) {
        logger.info(`${this.name}: '${s.title}' is a new show!`)
        const newShow = await new this.Model(s).save()
        return await this._updateNumSeasons(newShow)
      }

      logger.info(`${this.name}: '${found.title}' is an existing show.`)

      found.episodes.map(e => {
        const matching = s.episodes.find(
          s => s.season === e.season && s.episode === e.episode
        )

        if (e.first_aired > s.latest_episode) {
          s.latest_episode = e.first_aired
        }

        if (!matching) {
          return s.episodes.push(e)
        }

        s = this._updateEpisode(matching, e, s, '480p')
        s = this._updateEpisode(matching, e, s, '720p')
        s = this._updateEpisode(matching, e, s, '1080p')
      })

      return await this._updateNumSeasons(s)
    } catch (err) {
      logger.error(err)
    }
  }

  /**
   * Adds one seasonal season to a show.
   * @param {!AnimeShow|Show} show - The show to add the torrents to.
   * @param {!Object} episodes - The episodes containing the torrents.
   * @param {!number} season - The season number.
   * @param {!string} slug - The slug of the show.
   * @returns {undefined}
   */
  _addSeasonalSeason(
    show: AnimeShow | Show,
    episodes: Object,
    season: number,
    slug: string
  ): void {
    return trakt.seasons.season({
      id: slug,
      season,
      extended: 'full'
    }).then(traktEpisodes => {
      traktEpisodes.map(e => {
        if (!episodes[season][e.number]) {
          return
        }

        const episode = {
          tvdb_id: parseInt(e.ids['tvdb'], 10),
          season: parseInt(e.season, 10),
          episode: parseInt(e.number, 10),
          title: e.title,
          overview: e.overview,
          date_based: false,
          first_aired: new Date(e.first_aired).getTime() / 1000.0,
          torrents: episodes[season][e.number]
        }

        if (episode.first_aired > show.latest_episode) {
          show.latest_episode = episode.first_aired
        }

        episode.torrents[0] = episodes[season][e.number]['480p']
          ? episodes[season][e.number]['480p']
          : episodes[season][e.number]['720p']

        show.episodes.push(episode)
      })
    }).catch(err =>
      logger.error(`Trakt: Could not find any data on: ${err.path || err} with slug: '${slug}'`)
    )
  }

  /**
   * Adds one datebased season to a show.
   * @param {!AnimeShow|Show} show - The show to add the torrents to.
   * @param {!Object} episodes - The episodes containing the torrents.
   * @param {!number} season - The season number.
   * @returns {Promise<AnimeShow|Show|undefined>} - The show with a datebased
   * season attached.
   */
  async _addDateBasedSeason(
    show: AnimeShow | Show,
    episodes: Object,
    season: number
  ): Promise<AnimeShow | Show | void> {
    try {
      if (!show.tvdb_id || !episodes[season]) {
        return
      }

      const tvdbShow = await tvdb.getSeriesAllById(show.tvdb_id)
      tvdbShow.episodes.map(tvdbEpisode => {
        Object.keys(episodes[season]).map(e => {
          const d = new Date(tvdbEpisode.firstAired)
            .toISOString()
            .substring(0, 10)
          if (`${season}-${e.replace(/\./, '-')}` !== d) {
            return
          }

          const episode = {
            tvdb_id: parseInt(tvdbEpisode.id, 10),
            season: parseInt(tvdbEpisode.airedEpisodeNumber, 10),
            episode: parseInt(tvdbEpisode.airedSeason, 10),
            title: tvdbEpisode.episodeName,
            overview: tvdbEpisode.overview,
            date_based: true,
            first_aired: new Date(tvdbEpisode.firstAired).getTime() / 1000.0,
            torrents: episodes[season][e]
          }

          if (episode.first_aired > show.latest_episode) {
            show.latest_episode = episode.first_aired
          }

          if (!episode.season) {
            return
          }

          episode.torrents[0] = episodes[season][e]['480p']
            ? episodes[season][e]['480p']
            : episodes[season][e]['720p']

          show.episodes.push(episode)
        })
      })
    } catch (err) {
      logger.error(`TVDB: Could not find any data on: ${err.path || err} with tvdb_id: '${show.tvdb_id}'`)
    }
  }

  /**
   * Adds episodes to a show.
   * @param {!AnimeShow|Show} show - The show to add the torrents to.
   * @param {!Object} episodes - The episodes containing the torrents.
   * @param {!string} slug - The slug of the show.
   * @returns {Show} - A show with updated torrents.
   */
  addEpisodes(
    show: AnimeShow | Show,
    episodes: Object,
    slug: string
  ): Show {
    let { dateBased } = episodes
    delete episodes.dateBased
    dateBased = dateBased || show.dateBased

    return pMap(Object.keys(episodes), season => {
      if (dateBased) {
        return this._addDateBasedSeason(show, episodes, season)
      }

      return this._addSeasonalSeason(show, episodes, season, slug)
    }).then(() => this._updateEpisodes(show))
      .catch(err => logger.error(err))
  }

  /**
   * Get TV show images from TMDB.
   * @param {!number} tmdbId - The tmdb id of the show you want the images
   * from.
   * @returns {Object} - Object with banner, fanart and poster images.
   */
  _getTmdbImages(tmdbId: number): Object {
    return tmdb.tv.images({
      tv_id: tmdbId
    }).then(i => {
      const baseUrl = 'http://image.tmdb.org/t/p/w500'

      const tmdbPoster = i.posters.filter(
        poster => poster.iso_639_1 === 'en' || poster.iso_639_1 === null
      )[0].file_path
      const tmdbBackdrop = i.backdrops.filter(
        backdrop => backdrop.iso_639_1 === 'en' || backdrop.iso_639_1 === null
      )[0].file_path

      const { Holder } = AbstractHelper
      const images = {
        banner: tmdbPoster ? `${baseUrl}${tmdbPoster}` : Holder,
        fanart: tmdbBackdrop ? `${baseUrl}${tmdbBackdrop}` : Holder,
        poster: tmdbPoster ? `${baseUrl}${tmdbPoster}` : Holder
      }

      return this.checkImages(images)
    })
  }

  /**
   * Get TV show images from TVDB.
   * @param {!number} tvdbId - The tvdb id of the show you want the images
   * from.
   * @returns {Object} - Object with banner, fanart and poster images.
   */
  _getTvdbImages(tvdbId: number): Object {
    return tvdb.getSeriesById(tvdbId).then(i => {
      const baseUrl = 'http://thetvdb.com/banners/'

      const { Holder } = AbstractHelper
      const images = {
        banner: i.banner ? `${baseUrl}${i.banner}` : Holder,
        fanart: i.banner ? `${baseUrl}${i.banner}` : Holder,
        poster: i.banner ? `${baseUrl}${i.banner}` : Holder
      }

      return this.checkImages(images)
    })
  }

  /**
   * Get TV show images from Fanart.
   * @param {!number} tvdbId - The tvdb id of the show you want the images
   * from.
   * @returns {Object} - Object with banner, fanart and poster images.
   */
  _getFanartImages(tvdbId: number): Object {
    return fanart.getShowImages(tvdbId).then(i => {
      const { Holder } = AbstractHelper
      const images = {
        banner: i.tvbanner ? i.tvbanner[0].url : Holder,
        fanart: i.showbackground
          ? i.showbackground[0].url
          : i.clearart
            ? i.clearart[0].url
            : Holder,
        poster: i.tvposter ? i.tvposter[0].url : Holder
      }

      return this.checkImages(images)
    })
  }

  /**
   * Get TV show images.
   * @override
   * @protected
   * @param {!number} tmdbId - The tmdb id of the show you want the images
   * from.
   * @param {!number} tvdbId - The tvdb id of the show you want the images
   * from.
   * @returns {Promise<Object>} - Object with banner, fanart and poster images.
   */
  getImages({ tmdbId, tvdbId }: Object): Promise<Object> {
    return this._getTmdbImages(tmdbId)
      .catch(() => this._getTvdbImages(tvdbId))
      .catch(() => this._getFanartImages(tmdbId))
      .catch(() => AbstractHelper.DefaultImages)
  }

  /**
   * Get info from Trakt and make a new show object.
   * @override
   * @param {!string} id - The slug to query https://trakt.tv/.
   * @returns {Show} - A new show without the episodes attached.
   */
  async getTraktInfo(id: string): Show {
    try {
      const traktShow = await trakt.shows.summary({
        id,
        extended: 'full'
      })
      const traktWatchers = await trakt.shows.watching({ id })

      if (!traktShow) {
        return logger.warn(`No show found for slug: '${id}'`)
      }

      const { ids } = traktShow
      const { imdb, tmdb, slug, tvdb } = ids
      if (!imdb || !tmdb || !tvdb) {
        return logger.warn(`No ids found for slug: '${id}'`)
      }

      const images = await this.getImages({
        tmdbId: tmdb,
        tvdbId: tvdb
      })

      return {
        imdb_id: imdb,
        title: traktShow.title,
        year: traktShow.year,
        slug,
        synopsis: traktShow.overview,
        runtime: traktShow.runtime,
        rating: {
          votes: traktShow.votes,
          watching: traktWatchers ? traktWatchers.length : 0,
          percentage: Math.round(traktShow.rating * 10)
        },
        images,
        genres: traktShow.genres ? traktShow.genres : ['unknown'],
        tvdb_id: tvdb,
        country: traktShow.country,
        network: traktShow.network,
        air_day: traktShow.airs.day,
        air_time: traktShow.airs.time,
        status: traktShow.status,
        num_seasons: 0,
        last_updated: Number(new Date()),
        latest_episode: 0,
        episodes: []
      }
    } catch (err) {
      logger.error(`Trakt: Could not find any data on: ${err.path || err} with slug: '${id}'`)
    }
  }

}

// Export the necessary modules.
export MovieHelper from './MovieHelper'
export ShowHelper from './ShowHelper'

// Import the necessary modules.
// @flow
import {
  bulkConfigs,
  movieConfigs,
  showConfigs,
  ytsConfigs
} from './configs'
import {
  BulkProvider,
  MovieProvider,
  ShowProvider,
  YtsProvider
} from './providers'

/**
 * The max concurreny web requests at a time.
 * @type {number}
 */
const maxWebRequests: number = 2

/**
 * Export the providers to be attached to the PopApiScraper.
 * @type {Array<Object>}
 */
export default [{
  Provider: BulkProvider,
  args: {
    maxWebRequests,
    configs: bulkConfigs
  }
}, {
  Provider: MovieProvider,
  args: {
    maxWebRequests,
    configs: movieConfigs
  }
}, {
  Provider: ShowProvider,
  args: {
    maxWebRequests,
    configs: showConfigs
  }
}, {
  Provider: YtsProvider,
  args: {
    maxWebRequests,
    configs: ytsConfigs
  }
}]

// Import the necessary modules.
// @flow
import pMap from 'p-map'
import pTimes from 'p-times'
import { AbstractProvider } from 'pop-api-scraper'

import type {
  MovieHelper,
  ShowHelper
} from '../helpers'

/**
 * Class for scraping content from various sources.
 * @implements {AbstractProvider}
 * @type {BaseProvider}
 */
export default class BaseProvider extends AbstractProvider {

  /**
   * Map of the available content types to scrape.
   * @type {Object}
   */
  static ContentTypes: Object = {
    Movie: 'movie',
    Show: 'show'
  }

  /**
   * The api of the torrent provider.
   * @type {Object}
   */
  api: Object

  /**
   * The name of the torrent provider.
   * @type {string}
   */
  name: string

  /**
   * The helper class for adding movies.
   * @type {MovieHelper|ShowHelper}
   */
  helper: MovieHelper | ShowHelper

  /**
   * The type of content to scrape.
   * @type {string}
   */
  contentType: string

  /**
   * The max allowed concurrent web requests.
   * @type {number}
   */
  maxWebRequests: number

  /**
   * The query object for the api.
   * @type {Object}
   */
  query: Object

  /**
   * The regular expressions used to extract information about movies.
   * @type {Array<Object>}
   */
  regexps: Array<Object>

  /**
   * Gets information about a movie from Trakt.tv and insert the movie into the
   * MongoDB database.
   * @protected
   * @param {!Object} content - The content information.
   * @throws {Error} - 'movie' is not a valid value for Types!
   * @returns {Promise<Object, Error>} - A movie object.
   */
  _getMovieContent(content: Object): Promise<Object> {
    const { episodes, slug } = content
    if (episodes && episodes[0]) {
      delete episodes[0]
    }

    return this.helper.getTraktInfo(slug).then(res => {
      if (res && res.imdb_id) {
        return this.helper.addEpisodes(res, episodes, slug)
      }
    })
  }

  /**
   * Gets information about a show from Trakt.tv and insert the show into the
   * MongoDB database.
   * @protected
   * @param {!Object} content - The show information.
   * @throws {Error} - 'show' is not a valid value for Types!
   * @returns {Promise<Object, Error>} - A show object.
   */
  _getShowContent(content: Object): Promise<Object> {
    const { slugYear, torrents } = content
    return this.helper.getTraktInfo(slugYear).then(res => {
      if (res && res.imdb_id) {
        return this.helper.addTorrents(res, torrents)
      }
    })
  }

  /**
   * Gets information about content from Trakt.tv and inserts the content into
   * the MongoDB database.
   * @protected
   * @param {!Object} content - The content information.
   * @throws {Error} - 'CONTENT_TYPE' is not a valid value for Types!
   * @returns {Promise<Object>} - A content object.
   */
  getContent(content: Object): Promise<Object> {
    if (this.contentType === BaseProvider.ContentTypes.Movie) {
      return this._getShowContent(content)
    } else if (this.contentType === BaseProvider.ContentTypes.Show) {
      return this._getMovieContent(content)
    }

    const err = new Error(`'${this.contentType}' is not a valid value for ContentTypes!`)
    return Promise.reject(err)
  }

  /**
   * Extract content information based on a regex.
   * @abstract
   * @protected
   * @param {!Object} options - The options to extract content information.
   * @param {!Object} options.torrent - The torrent to extract the content
   * information.
   * @param {!Object} options.regex - The regex object to extract the content
   * information.
   * @param {?string} [lang] - The language of the torrent.
   * @throws {Error} - Using default method: 'extractContent'
   * @returns {Object|undefined} - Information about the content from the
   * torrent.
   */
  extractContent({ torrent, regex, lang }: Object): Object | void {
    throw new Error('Using default method: \'extractContent\'')
  }

  /**
   * Get content info from a given torrent.
   * @protected
   * @param {!Object} options - The options to get content info from a torrent.
   * @param {!Object} options.torrent - A torrent object to extract content
   * information from.
   * @param {!string} [optiosn.lang=en] - The language of the torrent.
   * @returns {Object|undefined} - Information about the content from the
   * torrent.
   */
  getContentData({ torrent, lang = 'en' }: Object): Object | void {
    const regex = this.regexps.find(
      r => r.regex.test(torrent.title) || r.regex.test(torrent.name)
    )

    if (regex) {
      return this.extractContent({
        torrent,
        regex,
        lang
      })
    }

    logger.warn(`${this.name}: Could not find data from torrent: '${torrent.title}'`)
  }

  /**
   * Attach the torrent object to the content.
   * @abstract
   * @protected
   * @param {!Object} options - The options to attach a torrent to the content.
   * @param {!Object} options.content - The content to attach a torrent to.
   * @param {!Object} options.torrent - The torrent object ot attach.
   * @param {!string} options.quality - The quality of the torrent.
   * @param {?number} options.season - The season number for the torrent.
   * @param {?number} options.episode - The episode number for the torrent.
   * @param {!string} [options.lang] - The language of the torrent.
   * @throws {Error} - Using default method: 'attachTorrent'
   * @returns {Object} - The content with the newly attached torrent.
   */
  attachTorrent({
    content,
    torrent,
    quality,
    season,
    episode,
    lang
  }: Object): Object {
    throw new Error('Using default method: \'attachTorrent\'')
  }

  /**
   * Put all the found content from the torrents in an array.
   * @abstract
   * @protected
   * @param {!Object} options - The options to get the content.
   * @param {!Array<Object>} options.torrents - A list of torrents to extract
   * content information from.
   * @param {!string} [options.lang=en] - The language of the torrents.
   * @throws {Error} - Using default method: 'getAllContent'
   * @returns {Promise<Array<Object>, Error>} - A list of object with
   * content information extracted from the torrents.
   */
  getAllContent({
    torrents,
    lang = 'en'
  }: Object): Promise<Array<Object>> {
    throw new Error('Using default method: \'getAllContent\'')
  }

  /**
   * Get all the torrents of a given torrent provider.
   * @protected
   * @param {!number} totalPages - The total pages of the query.
   * @returns {Promise<Array<Object>>} - A list of all the queried torrents.
   */
  getAllTorrents(totalPages: number): Promise<Array<Object>> {
    let torrents = []
    return pTimes(totalPages, async page => {
      this.query.page = page + 1

      logger.info(`${this.name}: Started searching ${this.name} on page ${page + 1} out of ${totalPages}`)
      const res = await this.api.search(this.query)
      const data = res.results
        ? res.results // Kat & ET
        : res.data
          ? res.data.movies // YTS
          : res.torrents
            ? res.torrents // Nyaa
            : []

      torrents = torrents.concat(data)
    }, {
      concurrency: 1
    }).then(() => {
      logger.info(`${this.name}: Found ${torrents.length} torrents.`)
      return torrents
    })
  }

  /**
   * Get the total pages to scrape for the provider query.
   * @protected
   * @returns {Promise<number>} - The number of total pages to scrape.
   */
  getTotalPages(): Promise<number> {
    return this.api.search(this.query).then(res => {
      if (res.data) { // Yts
        return Math.ceil(res.data.movie_count / 50)
      } else if (res.total_pages) { // Kat & ET
        return res.total_pages
      }

      return Math.ceil(res.totalRecordCount / res.queryRecordCount) // Nyaa
    })
  }

  /**
   * Set the configuration to scrape with.
   * @protected
   * @param {!Object} config - The config to get content with.
   * @param {!string} config.name - The name of the config.
   * @param {!Object} config.api - The API module ot get the content with.
   * @param {!string} config.contentType - The type of content to scrape.
   * @param {!MongooseModel} config.Model - The model for the content to
   * scrape.
   * @param {!IHelper} config.Helper - The helper class to save the content to
   * the database.
   * @param {?Object} config.query - The query to get the content with for the
   * api.
   * @param {?Array<Ojbect>} config.regexps - The regular expressions used to
   * extract information from a torrent.
   * @returns {undefined}
   */
  setConfig({
    name,
    api,
    contentType,
    Model,
    Helper,
    query,
    regexps
  }: Object): void {
    this.name = name
    this.api = api
    this.contentType = contentType
    this.helper = new Helper({
      Model,
      name
    })
    this.query = query
    this.regexps = regexps
  }

  /**
   * Get the contents for a configuration.
   * @override
   * @param {!Object} config - The config to get content with.
   * @param {!string} config.name - The name of the config.
   * @param {!Object} config.api - The API module ot get the content with.
   * @param {!string} config.contentType - The type of content to scrape.
   * @param {!MongooseModel} config.Model - The model for the content to
   * scrape.
   * @param {!IHelper} config.Helper - The helper class to save the content to
   * the database.
   * @param {?Object} config.query - The query to get the content with for the
   * api.
   * @param {?Array<Ojbect>} config.regexps - The regular expressions used to
   * extract information from a torrent.
   * @returns {Promise<Array<Object>|undefined, Error>} - The results of a
   * configuration.
   */
  async scrapeConfig({
    name,
    api,
    contentType,
    Model,
    Helper,
    query,
    regexps
  }: Object): Promise<Array<Object> | void> {
    try {
      this.setConfig({ name, api, contentType, Model, Helper, query, regexps })

      const totalPages = await this.getTotalPages()
      if (!totalPages) {
        return logger.error(
          `${this.name}: totalPages returned: '${totalPages}'`
        )
      }

      logger.info(`${this.name}: Total pages ${totalPages}`)

      const torrents = await this.getAllTorrents(totalPages)

      const { language } = this.query
      const allContent = await this.getAllContent({
        torrents,
        language
      })

      return await pMap(allContent, content => this.getContent(content), {
        concurrency: this.maxWebRequests
      })
    } catch (err) {
      logger.error(err)
    }
  }

}

// import the necessary modules.
// @flow
import pMap from 'p-map'

import BaseProvider from './BaseProvider'

/**
 * Class for scraping content from EZTV and HorribleSubs.
 * @extends {BaseProvider}
 * @type {BulkProvider}
 */
export default class BulkProvider extends BaseProvider {

  /**
   * Get the contents for a configuration.
   * @param {!Object} config - The config to get content with.
   * @param {!string} config.name - The name of the config.
   * @param {!Object} config.api - The API module ot get the content with.
   * @param {!string} config.contentType - The type of content to scrape.
   * @param {!MongooseModel} config.Model - The model for the content to
   * scrape.
   * @param {!IHelper} config.Helper - The helper class to save the content to
   * the database.
   * @returns {Promise<Array<Object>, Error>} - The results of a configuration.
   */
  scrapeConfig({
    name,
    api,
    contentType,
    Model,
    Helper
  }: Object): Promise<Array<Object>> {
    this.setConfig({ name, api, contentType, Model, Helper })

    logger.info(`${this.name}: Started scraping...`)
    return this.api.getAll().then(contents => {
      logger.info(
        `${this.name}: Found ${contents.length} ${this.contentType}s.`
      )

      return pMap(contents, c => {
        return this.api.getData(c)
          .then(content => this.getContent(content))
          .catch(err => logger.error(err))
      }, {
        concurrency: this.maxWebRequests
      })
    })
  }

}

// Import the necessary modules.
// @flow
import pMap from 'p-map'
import bytes from 'bytes'

import BaseProvider from './BaseProvider'
import movieMap from './maps/movieMap'

/**
 * Class for scraping movie content from various sources.
 * @extends {BaseProvider}
 * @type {MovieProvider}
 */
export default class MovieProvider extends BaseProvider {

  /**
   * Extract content information based on a regex.
   * @override
   * @protected
   * @param {!Object} options - The options to extract content information.
   * @param {!Object} options.torrent - The torrent to extract the content
   * information.
   * @param {!Object} options.regex - The regex object to extract the content
   * information.
   * @param {?string} [lang] - The language of the torrent.
   * @returns {Object|undefined} - Information about the content from the
   * torrent.
   */
  extractContent({ torrent, regex, lang }: Object): Object | void {
    let movieTitle
    let slug

    const {
      title, size, seeds, peers, magnet, torrentLink, fileSize
    } = torrent

    movieTitle = title.match(regex.regex)[1]
    if (movieTitle.endsWith(' ')) {
      movieTitle = movieTitle.substring(0, movieTitle.length - 1)
    }
    movieTitle = movieTitle.replace(/\./g, ' ')

    slug = movieTitle.replace(/[^a-zA-Z0-9 ]/gi, '')
      .replace(/\s+/g, '-')
      .toLowerCase()
    if (slug.endsWith('-')) {
      slug = slug.substring(0, slug.length - 1)
    }
    slug = slug in movieMap ? movieMap[slug] : slug

    const year = parseInt(title.match(regex.regex)[2], 10)
    const quality = title.match(regex.regex)[3]

    const torrentObj = {
      url: magnet || torrentLink,
      seeds: seeds || 0,
      peers: peers || 0,
      size: bytes(size),
      filesize: size || fileSize,
      provider: this.name
    }
    const movie = {
      movieTitle,
      slug,
      slugYear: `${slug}-${year}`,
      year,
      quality,
      language: lang,
      type: this.contentType,
      torrents: {}
    }

    return this.attachTorrent({
      movie,
      quality,
      lang,
      torrent: torrentObj
    })
  }

  /**
   * Attach the torrent object to the content.
   * @overridd
   * @protected
   * @param {!Object} options - The options to attach a torrent to the content.
   * @param {!Object} options.movie - The content to attach a torrent to.
   * @param {!Object} options.torrent - The torrent object ot attach.
   * @param {!string} options.quality - The quality of the torrent.
   * @param {!string} [options.lang] - The language of the torrent.
   * @returns {Object} - The content with the newly attached torrent.
   */
  attachTorrent({ movie, torrent, quality, lang }: Object): Object {
    if (!movie.torrents[lang]) {
      movie.torrents[lang] = {}
    }
    if (!movie.torrents[lang][quality]) {
      movie.torrents[lang][quality] = torrent
    }

    return movie
  }

  /**
   * Put all the found content from the torrents in an array.
   * @override
   * @protected
   * @param {!Object} options - The options to get the content.
   * @param {!Array<Object>} options.torrents - A list of torrents to extract
   * content information from.
   * @param {!string} [options.lang=en] - The language of the torrents.
   * @returns {Promise<Array<Object>, Error>} - A list of object with
   * content information extracted from the torrents.
   */
  getAllContent({
    torrents,
    lang = 'en'
  }: Object): Promise<Array<Object>> {
    const movies = new Map()

    return pMap(torrents, t => {
      if (!t) {
        return
      }

      const movie = this.getContentData({
        lang,
        torrent: t
      })

      if (!movie) {
        return
      }

      const { slug, language, quality } = movie
      if (!movies.has(slug)) {
        return movies.set(slug, movie)
      }

      const torrent = movie.torrents[language][quality]
      const created = this.attachTorrent({
        torrent,
        quality,
        language,
        movie
      })

      return movies.set(slug, created)
    }, {
      concurrency: 1
    }).then(() => Array.from(movies.values()))
  }

}

// Import the necessary modules.
// @flow
import pMap from 'p-map'

import BaseProvider from './BaseProvider'
import showMap from './maps/showMap'

/**
 * Class for scraping show content from various sources.
 * @extends {BaseProvider}
 * @type {ShowProvider}
 */
export default class ShowProvider extends BaseProvider {

  /**
   * Extract content information based on a regex.
   * @override
   * @protected
   * @param {!Object} options - The options to extract content information.
   * @param {!Object} options.torrent - The torrent to extract the content
   * information.
   * @param {!Object} options.regex - The regex object to extract the content
   * information.
   * @returns {Object|undefined} - Information about the content from the
   * torrent.
   */
  extractContent({ torrent, regex }: Object): Object | void {
    let episode
    let season
    let slug

    const { title, name } = torrent
    const t = regex.regex.test(title)
      ? title
      : regex.regex.test(name)
        ? name
        : null
    if (!t) {
      return
    }
    const match = t.match(regex.regex)

    const showTitle = match[1].replace(/\./g, ' ')
    slug = showTitle.replace(/[^a-zA-Z0-9\- ]/gi, '')
      .replace(/\s+/g, '-')
      .toLowerCase()
    slug = slug in showMap ? showMap[slug] : slug

    season = 1
    season = regex.dateBased ? parseInt(match[2], 10) : match[2]

    episode = match.length >= 4
      ? parseInt(match[3], 10)
      : parseInt(match[2], 10)
    episode = regex.dateBased ? parseInt(match[3], 10) : match[3]

    const quality = t.match(/(\d{3,4})p/) !== null
      ? t.match(/(\d{3,4})p/)[0]
      : '480p'

    const torrentObj = {
      url: torrent.magnet ? torrent.magnet : torrent.torrent_link,
      seeds: torrent.seeds ? torrent.seeds : 0,
      peers: torrent.peers ? torrent.peers : 0,
      provider: this.name
    }

    const show = {
      showTitle,
      slug,
      season,
      episode,
      quality,
      dateBased: regex.dateBased,
      episodes: {},
      type: this.contentType
    }

    return this.attachTorrent({
      show,
      season,
      episode,
      quality,
      torrent: torrentObj
    })
  }

  /**
   * Attach the torrent object to the content.
   * @override
   * @protected
   * @param {!Object} options - The options to attach a torrent to the content.
   * @param {!Object} options.show - The content to attach a torrent to.
   * @param {!Object} options.torrent - The torrent object ot attach.
   * @param {!string} options.quality - The quality of the torrent.
   * @param {?number} options.season - The season number for the torrent.
   * @param {?number} options.episode - The episode number for the torrent.
   * @throws {Error} - Using default method: 'attachTorrent'
   * @returns {Object} - The content with the newly attached torrent.
   */
  attachTorrent({
    show,
    torrent,
    season,
    episode,
    quality
  }: Object): Object {
    if (!show.episodes[season]) {
      show.episodes[season] = {}
    }
    if (!show.episodes[season][episode]) {
      show.episodes[season][episode] = {}
    }

    const qualityObj = show.episodes[season][episode][quality]
    if (
      (!qualityObj || show.showTitle.toLowerCase().indexOf('repack') > -1) ||
      (qualityObj && qualityObj.seeds < torrent.seeds)
    ) {
      show.episodes[season][episode][quality] = torrent
    }

    return show
  }

  /**
   * Put all the found content from the torrents in an array.
   * @override
   * @protected
   * @param {!Object} options - The options to get the content.
   * @param {!Array<Object>} options.torrents - A list of torrents to extract
   * content information from.
   * @returns {Promise<Array<Object>, Error>} - A list of object with
   * content information extracted from the torrents.
   */
  getAllContent({ torrents }: Object): Promise<Array<Object>> {
    const shows = new Map()

    return pMap(torrents, t => {
      if (!t) {
        return
      }

      const show = this.getContentData({
        torrent: t
      })
      if (!show) {
        return
      }

      const { slug, season, episode, quality } = show
      if (!shows.has(slug)) {
        return shows.set(slug, show)
      }

      const torrent = show.episodes[season][episode][quality]
      const created = this.attachTorrent({
        torrent,
        season,
        episode,
        quality,
        show
      })

      return shows.set(slug, created)
    }, {
      concurrency: 1
    }).then(() => Array.from(shows.values()))
  }

}

// Import the necessary modules.
// @flow
import MovieProvider from './MovieProvider'

/**
 * Class for scraping content from YTS.ag.
 * @extends {MovieProvider}
 * @type {YtsProvider}
 */
export default class YtsProvider extends MovieProvider {

  /**
   * Extract content information based on a regex.
   * @override
   * @protected
   * @param {!Object} options - The options to extract content information.
   * @param {!Object} options.torrent - The torrent to extract the content
   * information.
   * @param {?string} [lang] - The language of the torrent.
   * @returns {Object|undefined} - Information about the content from the
   * torrent.
   */
  extractContent({ torrent, lang }: Object): Object | void {
    const movie = {
      movieTitle: torrent.title,
      slug: torrent.imdb_code,
      slugYear: torrent.imdb_code,
      year: torrent.year,
      language: lang,
      torrents: {}
    }

    torrent.torrents.map(t => {
      // eslint-disable-next-line camelcase
      const { hash, peers, quality, seeds, size, size_bytes } = t

      const torrentObj = {
        url: `magnet:?xt=urn:btih:${hash}&tr=udp://glotorrents.pw:6969/announce&tr=udp://tracker.opentrackr.org:1337/announce&tr=udp://torrent.gresille.org:80/announce&tr=udp://tracker.openbittorrent.com:80&tr=udp://tracker.coppersurfer.tk:6969&tr=udp://tracker.leechers-paradise.org:6969&tr=udp://p4p.arenabg.ch:1337&tr=udp://tracker.internetwarriors.net:1337`,
        seeds: seeds || 0,
        peers: peers || 0,
        size: size_bytes,
        filesize: size,
        provider: this.name
      }

      return this.attachTorrent({
        movie,
        quality,
        lang,
        torrent: torrentObj
      })
    })

    return movie
  }

  /**
   * Get content info from a given torrent.
   * @override
   * @protected
   * @param {!Object} options - The options to get content info from a torrent.
   * @param {!Object} options.torrent - A torrent object to extract content
   * information from.
   * @param {!string} [optiosn.lang=en] - The language of the torrent.
   * @returns {Object|undefined} - Information about the content from the
   * torrent.
   */
  getContentData({ torrent, lang = 'en' }: Object): Object | void {
    if (
      torrent && torrent.torrents &&
      torrent.imdb_code &&
      torrent.language.match(/english/i)
    ) {
      return this.extractContent({
        torrent,
        lang
      })
    }

    logger.warn(`${this.name}: Could not find data from torrent: '${torrent.title}'`)
  }

}

